import React,{ useEffect } from "react";
import '../App.css';
import { Button } from './Button';
import './HeroSection.css';
import {Tick,MTLModel} from 'react-3d-viewer';

// render(){
//   return(
//     <div>
//      <MTLModel 
//         enableZoom = {false}
//         position={{x:0,y:-100,z:0}}
//         rotation={this.state.rotation}
//         texPath="./src/lib/model/"
//         mtl="./src/lib/model/freedom.mtl"
//         src="./src/lib/model/freedom.obj"
//      />
//     </div>
//   )
// }
// componentWillMount(){
//     this.tick.animate = false
// }
// componentDidMount(){
//   this.tick = Tick(()=>{
//     var {rotation} = this.state
//     rotation.y += 0.005
//     this.setState({rotation})

//   })
// }

useEffect(()=>{
  this.tick = Tick(()=>{
    var {rotation} = this.state
    rotation.y += 0.005
    this.setState({rotation})

  })
  return () => {
    this.tick.animate = false
  }
},[]);


function HeroSection() {
  return (
    <div className='hero-container'>
      <MTLModel 
        enableZoom = {false}
        position={{x:0,y:-100,z:0}}
        rotation={this.state.rotation}
        texPath="../lib/model/"
        mtl="../lib/model/machine.mtl"
        src="../lib/model/machine.obj"
     />
      <video src='/videos/video-6.mp4' autoPlay loop muted />
      <h1>KGT MAKİNE</h1>
      <p>Siz değerli müşterilerimiz ile sadece müşteri - satıcı ilişkisi olarak değil,</p>
      <p>iş partneri olmak hedefi ile yola çıkan KGT Makine her geçen gün kendini geliştirmektedir.</p>
      <div className='hero-btns'>
        <Button
          className='btns'
          buttonStyle='btn--outline'
          buttonSize='btn--large'
        >
          GET STARTED
        </Button>
        <Button
          className='btns'
          buttonStyle='btn--primary'
          buttonSize='btn--large'
          // onClick={console.log('hey')}
        >
          WATCH TRAILER <i className='far fa-play-circle' />
        </Button>
      </div>
    </div>
  );

}



export default HeroSection;
